package ControlStatement.IFELSE;

public class MaxTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numberOne = 10;
		int numberTwo = 30;
		int max = 0;
		if(numberOne>numberTwo) {
			max = numberOne;
		}else {
			max = numberTwo;
		}
		System.out.println("Max number is :" + max);
	}

}
